package com.mirrorview.domain.user.domain;import lombok.*;import javax.persistence.*;@Entity@NoArgsConstructor(access = AccessLevel.PROTECTED)@AllArgsConstructor@Getter@Builderpublic class Rating {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "rater")    private Member rater;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "rated")    private Member rated;    private float score;}